import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../App.css';
import LoadingSpinner from './LoadingSpinner'; // Import the LoadingSpinner



const DiagnosisPage = () => {
    const [file, setFile] = useState(null);
    const [diagnosis, setDiagnosis] = useState('');
    const [result, setResult] = useState('');
  
    // 파일을 선택할 때 호출되는 함수
    const handleFileChange = (event) => {
      setFile(event.target.files[0]);
    };
    // 파일을 서버로 업로드할 때 호출되는 함수
    const handleUpload = async () => {
      if (!file) {
        alert('파일을 선택해주세요.');
        return;
      }
  
      const formData = new FormData();
      formData.append('file', file);
  
      try {
        const response = await axios.post('http://localhost:5000/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
        // 진단 결과와 클래스 확률을 별도로 저장
        setDiagnosis(response.data.Diagnosis);
        const { Diagnosis, ...classProbs } = response.data;  // Diagnosis를 제외한 나머지 데이터
        setResult(JSON.stringify(classProbs, null, 2)); // JSON 문자열로 변환
      } catch (error) {
        console.error('Upload error', error);
        alert('업로드 실패!');
      }
    };
  
    return (
      <div>
        <h1>이미지 업로드 및 진단</h1>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleUpload}>진단</button>
        <div>
          <h3>Results:</h3>
          <p>{diagnosis}</p>  
          <pre>{result}</pre>  
        </div>
      </div>
    );
  };


  export default DiagnosisPage;
