import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';
import axios from 'axios';
import './App.css'; // CSS 파일을 가져옵니다.
import SignUp from './SignUp'; // SignUp 컴포넌트를 가져옵니다.

function App() {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState('');
  const [diagnosis, setDiagnosis] = useState('');

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) {
      alert('파일을 선택해주세요.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setDiagnosis(response.data.Diagnosis);
      const { Diagnosis, ...classProbs } = response.data;
      setResult(JSON.stringify(classProbs, null, 2));
    } catch (error) {
      console.error('업로드 오류', error);
      alert('업로드에 실패했습니다!');
    }
  };

  return (
    <Router>
      <div className="container">
        <h1>이미지 업로드 및 진단</h1>
        <Link to="/signup" className="signup-link">회원가입</Link>
        <Switch>
          <Route path="/signup">
            <SignUp />
          </Route>
          <Route path="/">
            <div className="upload-section">
              <input type="file" id="fileInput" className="custom-file-input" onChange={handleFileChange} />
              <label htmlFor="fileInput" className="upload-button">이미지 선택하기</label>
              <button onClick={handleUpload}>진단</button>
            </div> 
            <div className="results-section">
              <h3>진단 결과:</h3>
              <p>{diagnosis}</p>
              <h3>분류 결과:</h3>
              <pre>{result}</pre>
            </div>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
