import React, { useState } from 'react';
import axios from 'axios';
import './App.css'; // CSS 파일을 가져옵니다.

function App() {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('파일을 선택해주세요.'); // 파일 이름 상태 추가
  const [result, setResult] = useState('');
  const [diagnosis, setDiagnosis] = useState('');

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
    setFileName(selectedFile ? selectedFile.name : '파일을 선택해주세요.'); // 파일 이름 업데이트
  };

  const handleUpload = async () => {
    if (!file) {
      alert('파일을 선택해주세요.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setDiagnosis(response.data.Diagnosis);
      const { Diagnosis, ...classProbs } = response.data;
      setResult(JSON.stringify(classProbs, null, 2));
    } catch (error) {
      console.error('업로드 오류', error);
      alert('업로드에 실패했습니다.');
    }
  };

  return (
    <div className="container">
      <h1>이미지 업로드 및 진단</h1>
      <div className="upload-section">
        <input type="file" id="fileInput" className="custom-file-input" onChange={handleFileChange} />
        <label htmlFor="fileInput" className="upload-button">이미지 선택하기</label>
        <p className="file-name">{fileName}</p> {/* 파일 이름 표시 */}
        <button onClick={handleUpload}>진단</button>
      </div> 
      <div className="results-section">
        <h3>진단 결과:</h3>
        <p>{diagnosis}</p>
        <h3>분류 결과:</h3>
        <pre>{result}</pre>
      </div>
    </div>
  );
}

export default App;
